func buildDatasourceParams(selectedDatasources []string) string {
	if len(selectedDatasources) == 0 {
		return ""
	}
	params := url.Values{}
	for _, ds := range selectedDatasources {
		params.Add("datasource", ds)
	}
	return params.Encode()
}

